DROP TABLE IF EXISTS `changelog_type`;
CREATE TABLE IF NOT EXISTS `changelog_type` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `typeName` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

INSERT INTO `changelog_type` (`id`, `typeName`) VALUES
	(1, 'Website'),
	(2, 'Game');

DROP TABLE IF EXISTS `image_slider`;
CREATE TABLE IF NOT EXISTS `image_slider` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `image` varchar(255) DEFAULT NULL,
  `link` varchar(255) DEFAULT '#',
  `title` varchar(1000) DEFAULT NULL,
  `text` varchar(1000) DEFAULT NULL,
  `order` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

INSERT INTO `image_slider` (`id`, `image`, `link`, `title`, `text`, `order`) VALUES
	(1, '{path}slides/1.jpg', '', '{"english":"THE MOST COMPLETE WOTLK SERVER","polish":"PANEL GRACZA"}', '{"english":"There are no promises. Everything is already here and available. To learn more, please visit our \\"Why Sunwell\\" page in the Information menu. You won\'t be disappointed!","undefined":"","polish":"Rozbudowany panel gracza jest dostępny dla każdego. Zmiana ustawień, odblokowywanie postaci, system zabezpieczający Twoje konto i wiele więcej!"}', 2),
	(2, '{path}slides/2.jpg', '', '{"english":"GREAT PVP","polish":"EPICKIE PVP"}', '{"english":"All character spells and talents working, exceptionally low server-side delay, and much more!<br>Join us and enjoy the WotLK PvP at its best!","undefined":"","polish":"Wszystkie działające spelle klasowe i talenty! Tylko u nas możesz grać na tak wysokim poziomie, który jest efektem wieloletniej pracy doświadczonej ekipy."}', 3),
	(3, '{path}slides/3.jpg', '', '{"english":"EPIC PVE","polish":"ŚWIETNE PVE"}', '{"english":"All WotLK instances (raids and dungeons) and nearly all TBC instances have been completely rewritten by us. Every instance in the game is available. Play and feel the difference!","undefined":"","polish":"Wszystkie dostępne instancje WotLK (aż do Trial of the Crusader włącznie), w tym 5-many, są napisane w całości przez nas. Zagraj, a zobaczysz różnicę!"}', 4),
	(4, '{path}slides/4.jpg', '', '{"english":"REGULAR FIXES","undefined":"","polish":"CODZIENNE POPRAWKI"}', '{"english":"Years of constant development and we are not stopping. With passion and dedication, we have always strived to deliver to you the highest quality possible.","polish":"Codziennie poprawiamy błędy, dzięki czemu u nas działa to, co u innych nie będzie działało jeszcze przez lata! Lista wprowadzanych przez nas zmian jest widoczna w changelogu."}', 5);

DROP TABLE IF EXISTS `menu`;
CREATE TABLE IF NOT EXISTS `menu` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `name` text,
  `link` varchar(255) DEFAULT '#',
  `side` varchar(255) DEFAULT 'top',
  `rank` int(11) NOT NULL,
  `specific_rank` tinyint(1) NOT NULL DEFAULT '0',
  `order` int(11) DEFAULT NULL,
  `direct_link` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Is it a direct link or not? is needed for the ajax.',
  `permission` varchar(50) DEFAULT NULL,
  `idParent` int(10) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `FK_menu_ranks` (`rank`),
  CONSTRAINT `FK_menu_ranks` FOREIGN KEY (`rank`) REFERENCES `ranks` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=109 DEFAULT CHARSET=utf8;

