DROP TABLE IF EXISTS `pages`;
CREATE TABLE IF NOT EXISTS `pages` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `identifier` varchar(50) NOT NULL,
  `name` text,
  `content` text,
  `permission` varchar(50) DEFAULT NULL,
  `rank_needed` int(10) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`),
  UNIQUE KEY `identifier` (`identifier`),
  KEY `fk_rank_needed_ranks` (`rank_needed`),
  CONSTRAINT `fk_rank_needed_ranks` FOREIGN KEY (`rank_needed`) REFERENCES `ranks` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

INSERT INTO `pages` (`id`, `identifier`, `name`, `content`, `permission`, `rank_needed`) VALUES
	(1, 'connect', '{"english":"How to connect","polish":"Jak się połączyć"}', '{"english":"<div class=\\"jz-number\\">I</div><p>&nbsp;</p><div class=\\"jz-text\\">Download game client World of Warcraft: Wrath of the Lich King, which does not require installation and is already patched to 3.3.5a, using Torrent [ <a href=\\"https:/mega.co.nz/#!mFlVhKAb!cB32QAkLTyBE4_CMXx9njBxrEWLRodcj8aa6_dNsKQs\\" target=\\"_blank\\" data-mce-href=\\"https:/mega.co.nz/#!mFlVhKAb!cB32QAkLTyBE4_CMXx9njBxrEWLRodcj8aa6_dNsKQs\\">download</a> ]</div><p><br><br style=\\"clear: both;\\" data-mce-style=\\"clear: both;\\">&nbsp;<br>&nbsp;</p><div class=\\"jz-number\\">II</div><p>&nbsp;</p><div class=\\"jz-text\\">Edit (eg. in notepad) the file /World&nbsp;of&nbsp;Warcraft/data/enUS/realmlist.wtf,<br>remove all contents and paste the following:<br><strong style=\\"color: #2a557d;\\" data-mce-style=\\"color: #2a557d;\\">set realmlist logon.sunwell.pl</strong></div><p><br><br style=\\"clear: both;\\" data-mce-style=\\"clear: both;\\">&nbsp;<br>&nbsp;</p><div class=\\"jz-number\\">III</div><p>&nbsp;</p><div class=\\"jz-text\\">Register an account [ <a href=\\"http://sunwell.pl/register\\" data-mce-href=\\"http://sunwell.pl/register\\">here</a> ]</div><p><br><br style=\\"clear: both;\\" data-mce-style=\\"clear: both;\\">&nbsp;<br>&nbsp;</p><div class=\\"jz-number\\">IV</div><p>&nbsp;</p><div class=\\"jz-text\\">Enjoy playing on Sunwell server ;)<br>Launch the game using WoW.exe, found in the main game directory. To log in, please enter your username and not your e-mail address.</div><p><br><br style=\\"clear: both;\\" data-mce-style=\\"clear: both;\\">&nbsp;<br>&nbsp;</p><div class=\\"jz-number\\">V</div><p>&nbsp;</p><div class=\\"jz-text\\">Realm you play on can be chosen after logging in by clicking \\"Change realm\\" button over character list. A list of available realms, with statistics, is on the right. You can view more info by hovering your mouse over a realm.</div>","polish":"<p>TODO - switch to EN language.</p>"}', NULL, 1),
	(2, 'rules', '{"english":"Server rules","polish":"Regulamin"}', '{"english":"<ol><li>Creating an account means that you accept the Rules. By accepting the Rules you agree to follow them.</li><li>The server is available for everyone free of charge. We are devoting our free time when taking care of it, so please respect our work.</li><li>We will never ask for your account data. You are solely responsible for safety of your account and keeping account data undisclosed. The only places where you can safely type your account data are sunwell.pl website and the game client (assuming the realmlist is set correctly). Do not get fooled by contests requiring logging in on other websites! We are not responsible for account hijacking. We advise you to set up a password at least 8 characters long and consisting of letters, digits and special characters.</li><li>We can shutdown the server at any moment to carry out all necessary operations, for example updates.</li><li>It is strictly forbidden to buy, sell, trade things like: accounts, characters, Premium Points, gold - especially using auction sites, in-game chat, our Forum, social media.<br>Attempts of trading accounts, characters, Premium Points, gold - will be treated as theft attempts and punished with an appropriate penalty. Looking for \\"bored\\" players (eg. \\"looking for a bored shaman /w\\") and similar unclear terms will be treated as stealing attempts; in such situations you should clearly state why you are looking for someone.</li><li>We do not return lost accounts, characters, items, gold, etc. caused by disconnects, player\'s fault, technical problems or actions by a third party. We do not recover forgotten account data and we do not change account data manually.</li><li>Accounts that haven\'t logged in to game for more than 180 days are deleted automatically. It is impossible to recover anything from a deleted account. If you plan on taking a longer break from playing, you can freeze your account in Player\'s Panel, which will prevent its deletion.</li><li>We do not force anyone to play on our server. If you don\'t like it, change it. Do not spray your grief on our Forum or in the game, saying how bad the server or the administration is.</li><li>Advertising other servers will get you banned. Pseudoadvertisments are also advertisements.</li><li>By using Premium system features you declare that you are familiar with actual informations contained in&nbsp;<a href=\\"http://sunwell.pl/page/premium\\" target=\\"_blank\\" data-mce-href=\\"http://sunwell.pl/page/premium\\">Premium Guide</a>&nbsp;and you accept all rules in it. Premium system features and elements can be added or removed at any moment, without prior notice.</li><li>GameMaster\'s character can be recognized by &lt;GM&gt; tag before character\'s name, and by blue BLIZZ icon on the chat. The icon should appear before GameMaster\'s name. A player can ask a GM to prove his position by summoning (teleporting) the player.</li><li>Game Masters are helping you voluntarily, devoting their free time, so please respect their work and do not criticize them on every occasion.</li><li>GM Tickets submitted in the game must follow&nbsp;<a href=\\"http://sunwell.pl/page/ticketrules\\" target=\\"_blank\\" data-mce-href=\\"http://sunwell.pl/page/ticketrules\\">ticket rules</a>. Tickets breaking those rules will be closed without notice.</li><li>Slandering the server, administrators, Game Masters ANYWHERE is strictly forbidden. Do not confuse this with constructive criticism, to which we are open. If you accuse a GM without any proofs, be ready for consequences. Such matters are best settled with an Administrator using private messages on our Forum. Every report will be examined according to adequate procedures.</li><li>Impersonating or pretending to be an Administrator / Game Master / Developer, saying you know them or suggesting any kind of agreement with them is forbidden.</li><li>Breaking of Rules can be reported in a dedicated section on the Forum. Complaints are examined openly, we do not hide our decisions from the community.</li><li>You can appeal against an applied penalty by writing in dedicated section on the Forum. Appeals are public, we do not hide our decisions from the community.</li><li>After a penalty is imposed, you have 14 days to appeal. You can not appeal against penalties given by anticheat system.</li><li>A player can appeal against GameMaster\'s decision by contacting an Administrator. Administrator\'s decision is final.</li><li>Administration has the right to ban anyone whose behavior clearly harms the server.</li><li>Using bugs for your own benefits is absolutely forbidden. Players are obligated to inform administration (using BugTracker system) about every bug they spot. A GameMaster decides about a penalty for using bugs, considering intentions, benefits and risks comming from the cheating attempt.</li><li>Benefits obtained by a player in an unfair way will be removed and a penalty may (but doesn\'t have to) be applied.</li><li>Legitimate suspicion of mass Rules violation will result in penalty equal to penalty for the charges.</li><li>Fixing arena and battleground matches is forbidden. It is forbidden to consult arena queue joining with other teams. Joining without intending to fight is forbidden. The Administration records informations about the fights and in case of any doubts consequences will be drawn.</li><li>It is forbidden to run casino games, send COD mails to trick people into losing gold, or use similar tricks to rob other players.</li><li>It is forbidden to use quest items or mounts to attack other players. Quest stuff is needed during the quest and is meant only to be used for this purpose.</li><li>It is forbidden to swindle other players by breaking agreed loot rules. Master Looter, who has the room to abuse, should announce loot distribution rules to every group member before the raid.<br>If you are a group member, take a screenshot of the rules, in case they are broken. If the Master Looter doesn\'t announce them, be sure to ask! If something is unclear to you (who has the priority in rolling, can you roll for offspec, which items are reserved, when the loot is distributed, etc.) or said in an unclear fashion - ask accurately and take a screenshot!<br>If you are the Master Looter, take a screenshot of public announcing of the loot distribution rules, just in case someone accuses you of breaking them.<br>When writing a complaint, rule breaking has to be clearly visible on the screenshots.<br>Changing Master Looter doesn\'t influence previously agreed loot distribution rules.</li><li>It is forbidden to Need roll items to sell them or give them away (for this purpose there is Greed option).</li><li>Insulting others on public chats is forbidden. We will intervene only in serious cases, in other cases please use /ignore function. For exceptional vulgarity on /whisper you may also get punished.</li><li>It is forbidden to reveal someone\'s identity or personal informations, especially to embarass or harm them.</li><li>Character name in the game cannot be vulgar, insulting, racist, controversial, and shouldn\'t be a variation of Sunwell Staff members\' names. Please try to avoid provocative names, which could annoy followers of other religions.<br>The same applies to guild names, arena team names, pet names, etc.</li><li>Public chat spamming with messages or emotes will be punished with a kick/mute or even a ban.</li><li>On public chats it\'s forbidden to use vulgarisms excessively, write racist / tasteless jokes and controversial messages. We call for keeping a culture of speech. Deformations of vulgarisms, eg. mothaf**ka, k*rva, will be treated exacly as vulgarisms they represent.</li><li>There are custom global channels, which are considered public chats and have precisely defined rules:<br><br><strong>GLOBAL:&nbsp;</strong>multipurpose channel (temporary until more english speaking players join the server). There are no restrictions on channel usage (conversations, lfg, trading, etc.). The only allowed language is English. Please do not spam with lfg / trading announcements.<br><strong>WORLD:&nbsp;</strong>only for casual conversations, helping others, asking questions, etc. This channel is not meant for trading, looking for group, looking for arena partner, guild advertising, etc. Allowed languages are Polish (for now) and English.<br><strong>LFG:&nbsp;</strong>only for looking for a group for an instance / battleground / quest / achievement / capital raid / etc. Allowed languages are Polish (for now) and English.<br><strong>TRADING:&nbsp;</strong>only for trading. Linking a profession or asking to do so is considered trading. Allowed languages are Polish (for now) and English.<br><strong>GUAR:&nbsp;</strong>only for looking for a guild or advertising one, and for looking for an arena team or advertising one. You can find dedicated sections for Guilds and PvP on our Forum. Allowed languages are Polish (for now) and English.<br><br>Breaking channel rules, breaking server Rules, trolling, being rude - will result in channel blocking, which may be applied only by GameMasters and designated moderators, so you can\'t appeal against it. Appealing against a ban on those channels will result in being banned for 7 days for not reading the Rules. Posts in other sections of Forum with unban requests will be treated exactly the same. A ban on those channels lasts exactly 60 days and will go off automatically. It is forbidden to spam, group spam (lots of players write the same or similar message) and trash the channel with meaningless messages.<br>Being disconnected, losing an item, etc. - are not an excuse!<br>Channel blocking does not rule out other punishment for breaking server Rules.<br><br>If you don\'t like the rules on any of the channels, then don\'t use it. Criticizing channel rules or provoking GMs and moderators maintaining the channels will be punished with channel blocking.</li><li>It is forbidden to fool other players by announcing new expansion release on our server (Cataclysm, Mists of Pandaria, Warlords of Dreanor, Legion). The only place in which such important informations are delivered is our Forum. Such behavior will lead to being muted / channel blocking. Asking about new expansions, exclamations like Cata!, MoP!, WoD!, etc. - especially right before or right after server restart - will be considered trolling and fooling other players, and thus will lead to being muted / channel blocking.</li><li>We do not interfere in PvP situations. You are allowed to kill, torture and camp without limitations, however we ask for moderation.</li><li>It is forbidden to use software modifying game client and/or altering/crafting packets sent to the server. It is forbidden to use software automating activities in the game and/or on the website. Violating this rule will be punished with a permanent ban on all accounts and eventually IP address.</li><li>Possession of multiple accounts is allowed to a degree. In fact we are unable to check whather second account belongs to your brother, sister, friend or anyone else. However if there happens a situation in which one of the accounts broke the Rules, all the accounts using same IP address may be punished.<br>Multiacc by itself is not punishable, but please don\'t use the fact of having multiple accounts to gain additional benefits, or to break the Rules. Multiboxing is in fact gaining additional benefits, so it is forbidden.</li><li>The user agrees to use Sunwell website and Sunwell game server (together called the \\"service\\") at the user\'s sole risk. Neither Sunwell, nor anyone associated with Sunwell, warrants uninterrupted or error-free service. The service is provided on an \\"as is\\" and \\"as available\\" basis. Sunwell is not responsible for any damages arasing from the use of the service. Sunwell reserves the right to change, suspend or limit any aspect of the service at any time.</li><li>Sunwell may suspend or terminate any user\'s access to all or part of the service, for any conduct that Sunwell, in its sole discretion, believes is in violation of the Rules.</li><li>A player should visit our website and the Forum often to be up to date.</li><li>On the matters not covered by these Rules, principles of honesty, morality and common sense apply.</li><li>A player is obligated to track changes in the Rules. Logging in to game or using the website means that you accept the Rules in actual form.</li></ol><p><span>&nbsp;</span><br><strong>16.10.2015 - Changed paragraph number 5.</strong><span>&nbsp;&nbsp;</span><br><strong>15.10.2015 - Changed paragraph number 10.</strong><span>&nbsp;&nbsp;</span><br><strong>29.09.2015 - Rules were reformulated.</strong></p>","polish":"<ol><li>Sunwell jest prywatnym serwerem WoW. Całkowitą władzę nad nim sprawuje administracja, która wszystkie decyzje podejmuje niezależnie, chociaż niewykluczone, że zdanie graczy będzie miało wpływ na nasze decyzje.</li><li>Regulamin Sunwell jest najwyższym aktem stanowiącym prawo na serwerze. Każdy gracz w chwili założenia konta zobowiązuje się do jego przestrzegania.</li><li>Administracja zajmuje się serwerem całkowicie za darmo, nie czerpiąc z tego najmniejszych korzyści materialnych, prócz satysfakcji, dlatego prosimy o uszanowanie naszej pracy.</li><li>Nie zmuszamy nikogo do gry na naszym serwerze. Jeśli Ci nie odpowiada, zmień go na inny. Nie wylewaj swoich żali na forum i w grze jaki to serwer bądź administracja jest zła.<br>Reklamowanie innych serwerów będzie skutkowało banicją, aby ułatwić takiej osobie pójście na reklamowany serwer.</li><li>Administracja ma prawo w każdej chwili wyłączyć serwer, aby przeprowadzić niezbędne aktualizacje, bądź konserwacje, bez względu na to, czy ktoś akurat kończy bić bossa.</li><li>Administracja NIGDY nie pyta gracza o dane do konta. Sami odpowiadacie za jego odpowiednie zabezpieczenie i utrzymanie danych w tajemnicy. Jedynym miejscem, gdzie można wpisywać dane do konta jest strona sunwell.pl oraz klient gry przy zastrzeżeniu, że realmlist jest ustawiony prawidłowo. Nie dajcie się nabrać na jakieś konkursy organizowane na dziwnych stronach i inne sztuczki! My nie ponosimy żadnej odpowiedzialności za włamania na konta. Sugerowana długość hasła to co najmniej 8 znaków - mieszanina liter i cyfr.</li><li>Handel kontami i postaciami poza przeznaczonym do tego miejscem (Slave Market) jest zabroniony. Szukanie w grze chętnych do wymiany/kupna/sprzedaży, zachęcanie do rozmów na priv, ogłaszanie wystawionej postaci, zapraszanie do kupienia/wymiany postaci, itp. - będą traktowane jak próby kradzieży i stosownie karane.<br>Szukanie \\"znudzonych\\" graczy (i wszelkie inne niejasne określenia) będą traktowane jak oferty wymiany/kupna/sprzedaży i stosownie karane. Wystarczy jasno napisać po co szukasz i problemu nie będzie.<br>Szukanie właścicieli postaci wystawionych w Slave Markecie jest zabronione i także będzie traktowane jak próba kradzieży. Jeśli informacje o postaci widoczne w armory Ci nie wystarczają, to się za nią nie wymieniaj.<br>Istnieje całkowity zakaz kupna, sprzedaży, handlu takimi rzeczami jak: konta, postacie, punkty premium, gold, itp. - poza przeznaczonymi do tego miejscami, a w szczególności w serwisach typu allegro, w grze, na forum i innych podobnych miejscach.</li><li>Nie zwracamy utraconych w jakikolwiek sposób kont, postaci, itemów, itd.. Nie odzyskujemy również zapomnianych danych do waszych kont. Nie zmieniamy żadnych danych do kont ręcznie.</li><li>Administracja ma prawo usunąć konta, które nie logowały się do gry przez ponad 150 dni. Jeżeli planujesz dłuższą przerwę w graniu, to masz możliwość zamrożenia konta w panelu gracza.</li><li>Postać Game Mastera można rozpoznać po tagu przy imieniu postaci oraz niebieskiej ikonie BLIZZ na czacie. Proszę zwrócić uwagę, by ów ikona znajdowała się PRZED NICKIEM osoby podającej się za GM\'a. Gracz ma prawo poprosić GM\'a o udowodnienie swojej pozycji poprzez teleportowanie gracza do siebie.</li><li>Game Master pomaga tylko w sytuacjach, w których działanie emulatora/bazy jest nieprawidłowe. GM\'i pomagają Wam bezinteresownie, więc prosi się o uszanowanie ich pracy i nie krytykowanie ich na każdym kroku.</li><li>Szkalowanie i/lub oczernianie GM\'ów, serwera i administracji GDZIEKOLWIEK jest surowo zabronione. Nie mylić tego oczywiście z konstruktywną krytyką, na którą wszyscy jesteśmy otwarci. Jeśli oskarżasz GM\'a nie mając żadnych dowodów, to licz się z konsekwencjami w postaci bana. Takie sprawy najlepiej załatwiać z administracją via priv. Nikt tu nikogo nie ma zamiaru chronić, a każde doniesienie będzie sprawdzone według odpowiednich procedur.</li><li>Powoływanie się na znajomości z GM\'em / administratorem lub jakiekolwiek sugerowanie podobnych układów jest bezwzględnie zabronione.</li><li>Emulator z którego korzystamy - TrinityCore2, jak każdy, posiada zalety i wady (my jednak jesteśmy przekonani o znacznej przewadze tych pierwszych), dlatego zmuszeni będziemy do wprowadzenia pewnych zakazów i nakazów, które Wy zobligowani jesteście znać i przestrzegać. To w waszym interesie leży znajomość aktualnych zakazów dotyczących pewnych czynności uznawanych za bugowanie.</li><li>Na serwerze panuje bezwzględny zakaz bugowania, czyli omijania mechaniki gry na własną korzyść. Gracze są zobligowani powiadomić administrację (poprzez BugTracker) o każdym zauważonym błędzie. O karze za używanie bugów decyduje GM, mając na uwadze umyślność, korzyści oraz zagrożenia wynikające z próby oszukania emulatora.</li><li>Zabrania się wykorzystywania itemów i mountów z questów do atakowania innych graczy. Elementy potrzebne podczas wykonywania questa służą tylko i wyłącznie do wykonania questa.</li><li>Zabrania się oszukiwania innych graczy poprzez łamanie ustalonych zasad podziału lootu. Master Looter, który ma pole do nadużyć, powinien przed raidem przedstawić zasady podziału lootu każdemu uczestnikowi raidu.<br>Jeśli jesteś uczestnikiem, zrób screenshot podanych zasad, na wypadek gdyby zostały złamane. Jeśli Master Looter ich sam nie przedstawia, koniecznie o nie spytaj! Jeśli coś jest dla Ciebie niejasne (kto ma pierwszeństwo przy rollu, czy możesz rollować pod offspec, jakie itemy są zajęte, kiedy jest rozdawany loot, itd.) lub napisane niejednoznacznymi słowami - dopytaj dokładnie i zrób screenshot!<br>Jeśli jesteś Master Looterem, zrób screenshot publicznego ogłaszania zasad innym uczestnikom, na wypadek gdyby ktoś Cię oskarżał o łamanie wcześniej ustalonych zasad.<br>W przypadku skargi, złamanie zasad ma być jednoznacznie widoczne na screenshotach. Jeśli wymaga to wykonania 10 screenshotów, to ma ich być 10.<br>Zmiana Master Lootera nie wpływa na ustalone wcześniej zasady.</li><li>Podejrzenie o partycypację w zbiorowym łamaniu regulaminu jest karane na równi z przewinieniem.</li><li>Zakazuje się urządzania ustawionych walk na Arenach i Battlegroundach. Podstawianie się bez walki jest surowo zabronione. Administracja prowadzi pełną ewidencję wyników walk i w przypadku jakichkolwiek nieścisłości konsekwencje zostaną szybko wyciągnięte.</li><li>Korzyści osiągnięte przez gracza w sposób nieuczciwy będą usunięte i mogą (ale nie muszą) wiązać się z karą. Handel lootem, czyli oddawanie itemów (które powinny być dostępne tylko dla osób walczących z bossem) komuś innemu, jest zakazany. Zakazuje się także need\'owania itemów w celu odsprzedaży lub oddania komuś innemu (do tego służy opcja greed).</li><li>Zakazuje się urządzania gier typu kasyno, wysyłania maili COD w celu wyłudzenia golda i wszelkich tego typu sztuczek.</li><li>Na serwerze panuje bezwzględny zakaz obrażania innych graczy na kanałach publicznych. Za wyjątkowe chamstwo na /whisper także można zostać ukaranym.</li><li>Gracz może odwoływać się od decyzji GM poprzez rozmowę z administratorem. Od decyzji administratora nie ma odwołania.</li><li>Możliwość apelacji przysługuje tylko 7 dni od momentu otrzymania kary (ban, mute, itp.).<br>Od banów z anticheata nie ma odwołania.</li><li>W szczególnych przypadkach, administracja ma prawo zbanować każdego, jeśli uzna, że jest to podyktowane dobrem serwera i jest uzasadnione.</li><li>Gracz ma obowiązek szanować i stosować się do decyzji administracji oraz często odwiedzać forum i stronę serwera, aby być na bieżąco.</li><li>Korzystanie z systemu Premium jest dobrowolne. Gracz dokonujący dotacji jest tego całkowicie świadomy. Dotacje bezwzględnie nie podlegają zwrotowi.</li><li>Sunwell posiada serwer dedykowany, który niestety trzeba opłacać. Pieniądze pochodzą z systemu Premium. Jeśli zaistnieje taka sytuacja, że nie uzbieramy pełnej sumy, serwer będzie wyłączony tak długo, aż dana suma zostanie uzbierana.</li><li>Jedynym bezpiecznym miejscem do handlu Punktami Premium (PP) jest bazar, znajdujący się w panelu gracza. Jeśli zostaniesz okradziony handlując PP w jakikolwiek inny sposób, administracja nie ponosi za to odpowiedzialności. Robisz to na własne ryzyko.</li><li>Posiadanie wielu kont jest w pewnym stopniu dozwolone. Tak naprawdę nie jesteśmy w stanie sprawdzić czy drugie konto należy do brata, siostry, kolegi z akademika lub kogokolwiek innego. Jeśli jednak zdarzy się sytuacja, w której jedno z kont złamało regulamin, to wszystkie konta z tego adresu IP mogą zostać ukarane.<br>Po przegranej walce lepiej nie wchodzić na konto z przeciwną frakcją i wyzywać przeciwnika, bo oba konta zostaną stosownie ukarane.<br>Posiadanie multiacc samo w sobie nie jest już karalne, ale proszę nie wykorzystywać faktu posiadania wielu kont dla uzyskania dodatkowych korzyści, bądź łamania regulaminu. Multiboxing i wiele innych rzeczy to czerpanie dodatkowych korzyści, a więc są zakazane.</li><li>Nick postaci w grze nie może być wulgarny, obraźliwy, rasistowski, nie powinien być wariacją nicku nikogo z zarządu serwera. Nazywając swoją postać starajcie się nie używać nicków prowokacyjnych, mogących urazić wyznawców innych religii. Nie wzbudzajcie kontrowersji nickiem na siłę, \\"bo jest fajny\\".<br>To samo tyczy się nazw gildii, teamów arenowych, petów, itd..</li><li>Administracja nie ingeruje w rozgrywkę między graczami. Możecie zabijać, maltretować i kampić do woli, jednak apelujemy o zdrowy rozsądek.</li><li>Zakazuje się używania programów modyfikujących klienta gry i/lub fałszujących pakiety wysyłane do serwera. Zakazuje się używania programów automatyzujących działania w grze i/lub na stronie www. Niestosowanie się będzie skutkować permanentnym banem na konta z gry i ewentualnym banem IP.</li><li>Zakazuje się używania ikonek Blizzarda na wszelkiego rodzaju chatach. Kary od mute po bany, jeśli zostały użyte w celu wyłudzenia haseł/kont.</li><li>Zakazuje się spamu na chatach publicznych oraz spamu emotami. Kary od kick/mute po ban.</li><li>Na chatach publicznych obowiązuje zakaz przeklinania, używania wulgaryzmów, wypisywania rasistowskich żartów oraz wszelkich kontrowersyjnych i niesmacznych tekstów. Apelujemy o zachowanie kultury wypowiedzi. Wszelkie zniekształcenia wulgaryzmów, typu k*rva, yeb*ć, itp. są traktowane jak wulgaryzmy.</li><li>Istnieją specjalne kanały globalne, które są uznane za chaty publiczne i mają ściśle określone zasady:<br><br><strong>WORLD:&nbsp;</strong>służy wyłącznie do luźnych rozmów, pomocy innym, zadawania pytań, itp.. Handel, szukanie grupy, szukanie partnerów arenowych, reklamowanie gildii i inne podobne będą skutkowały blokadą kanału. Wulgaryzmy, trollowanie, chamstwo i łamanie regulaminu także będą nagradzane blokadą kanału.<br><strong>LFG:&nbsp;</strong>służy wyłącznie do szukania grupy na instancje, battlegroundy, grupowe questy/achievementy, wypady na przeciwną frakcję (nie gildii, nie teamu arenowego!). Rozmowy na inny temat, wulgaryzmy, trollowanie, chamstwo i łamanie regulaminu w jakikolwiek inny sposób będą skutkowały blokadą kanału.<br><strong>HANDEL:&nbsp;</strong>służy wyłącznie do handlu (prośba o pokazanie profesji, linkowanie profesji - to także handel). Rozmowy na inny temat, wulgaryzmy, trollowanie, chamstwo i łamanie regulaminu w jakikolwiek inny sposób będą skutkowały blokadą kanału.<br><strong>GUAR:&nbsp;</strong>służy wyłącznie do szukania/reklamowania gildii i szukania/reklamowania teamów arenowych. Rozmowy na inny temat, wulgaryzmy, trollowanie, chamstwo i łamanie regulaminu w jakikolwiek inny sposób będą skutkowały blokadą kanału. Na naszym forum dyskusyjnym można też znaleźć działy z gildiami i do PvP.<br><strong>PL:&nbsp;</strong>kanał globalny na realmie Stormrage, na którym nie ma zastrzeżeń co do tematyki rozmów, ponieważ niższa populacja nie powoduje spamu. Wulgaryzmy, trollowanie, chamstwo i łamanie regulaminu w jakikolwiek sposób będą skutkowały blokadą kanału.<br><br>Złamanie zasad lub regulaminu na kanale jest nagradzane blokadą kanału, która może być nałożona wyłącznie przez GameMasterów i wyznaczonych moderatorów, w związku z tym nie ma od niej odwołania. Odwoływanie się od bana na w/w kanałach będzie skutowało banicją konta na okres 7 dni za nieznajomość regulaminu; posty w innych działach na forum z prośbami o zdjęcie bana będą traktowane w ten sam sposób. Bany na tych kanałach trwają 3 miesiące i po tym czasie są automatycznie zdejmowane. Zakazuje się spamu, grupowego spamu (wiele osób pisze taką samą lub podobną wiadomość w sposób zorganizowany) oraz zaśmiecania kanału wiadomościami bez sensu.<br>Disconnect, utrata itemu, złamanie regulaminu przez brata (siostrę, wujka, psa...) lub jakakolwiek inna sytuacja nie są usprawiedliwieniem!<br><br>Zakazane jest wprowadzanie innych graczy w błąd poprzez zapowiadanie wejścia nowego dodatku (Cataclysm, Mists of Pandaria, Warlords of Dreanor). Jedynym miejsce gdzie przekazywane są tak ważne informacje, jest forum serwera. Takie zachowanie będzie skutkowało blokadą kanału. Pytania o wejście nowych dodatków, wykrzyknienia typu Cata!, MoP!, WoD!, itp. - w szczególności tuż przed lub po restarcie serwera - są uznawane za trollowanie i wprowadzanie innych w błąd, więc będą skutkowały blodaką kanału.<br><br>Jeśli nie podobają Ci się zasady panujące na którymkolwiek z kanałów, to na niego nie wchodź. Krytykowanie zasad tam panujących, bojkotowanie kanałów, prowokowanie GMów i moderatorów pilnujących porządku będzie nagradzane blokadą kanału, a w przypadku recydywy banicją konta.</li><li>W kwestiach nieopisanych przez regulamin obowiązują ogólne zasady uczciwości, moralności i zdrowego rozsądku.</li><li>Gracz jest zobowiązany do śledzenia zmian w regulaminie. Logowanie się do gry oraz korzystanie ze strony www oznaczają akceptację jego aktualnej treści.</li></ol><p>&nbsp;<br><strong>Ostatnia zmiana: 04.04.2015r. Zmienione punkty 33, 39: zakaz wspomagania programami teraz obejmuje także stronę www.</strong>&nbsp;<br><br><strong>Poprzednia zmiana: 12.10.2014r. Zmieniony punkt 4.</strong></p>"}', NULL, 1),
	(3, 'whysunwell', '{"english":"Why Sunwell","polish":"Dlaczego Sunwell"}', '{"english":"<p class=\\"onas-paragraph\\" style=\\"line-height: 18px;\\" data-mce-style=\\"line-height: 18px;\\">‣ <a href=\\"#introduction\\" data-mce-href=\\"#introduction\\">A few words of introduction</a><br> ‣ <a href=\\"#devstate\\" data-mce-href=\\"#devstate\\">Current development state</a><br> ‣ <a href=\\"#performance\\" data-mce-href=\\"#performance\\">Server performance</a><br> ‣ <a href=\\"#movemaps\\" data-mce-href=\\"#movemaps\\">MoveMaps</a><br> ‣ <a href=\\"#spellsystem\\" data-mce-href=\\"#spellsystem\\">Spell system</a><br> ‣ <a href=\\"#spellstacking\\" data-mce-href=\\"#spellstacking\\">Spell stacking</a><br> ‣ <a href=\\"#hotdot\\" data-mce-href=\\"#hotdot\\">HoT and DoT calculation</a><br> ‣ <a href=\\"#instanceid\\" data-mce-href=\\"#instanceid\\">Instance ID system</a><br> ‣ <a href=\\"#dungeonfinder\\" data-mce-href=\\"#dungeonfinder\\">Dungeon Finder</a><br> ‣ <a href=\\"#bgqueues\\" data-mce-href=\\"#bgqueues\\">Battleground Queues</a><br> ‣ <a href=\\"#transports\\" data-mce-href=\\"#transports\\">Transport System</a><br> ‣ <a href=\\"#instances\\" data-mce-href=\\"#instances\\">Instances</a><br> ‣ <a href=\\"#quests\\" data-mce-href=\\"#quests\\">Quests</a><br> ‣ <a href=\\"#pets\\" data-mce-href=\\"#pets\\">Pets</a><br> ‣ <a href=\\"#battlegrounds\\" data-mce-href=\\"#battlegrounds\\">Battlegrounds and arenas</a><br> ‣ <a href=\\"#dynamicmmr\\" data-mce-href=\\"#dynamicmmr\\">Dynamic MMR</a><br> ‣ <a href=\\"#achievements\\" data-mce-href=\\"#achievements\\">Achievements</a><br> ‣ <a href=\\"#events\\" data-mce-href=\\"#events\\">Ingame seasonal events</a><br> ‣ <a href=\\"#wintergrasp\\" data-mce-href=\\"#wintergrasp\\">Wintergrasp</a><br> ‣ <a href=\\"#raidbrowser\\" data-mce-href=\\"#raidbrowser\\">Raid Browser</a><br> ‣ <a href=\\"#arenaspectator\\" data-mce-href=\\"#arenaspectator\\">Arena Spectator</a><br> ‣ <a href=\\"#summary\\" data-mce-href=\\"#summary\\">Summary</a></p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"introduction\\"></a>A few words of introduction</p><p class=\\"onas-paragraph\\">Sunwell Server has existed in its current form since July 2008, while our community is even older and reaches back to the end of 2005. We\'ve been constantly developing our current emulator and been fixing bugs in it since the end of 2009. It is nearly impossible to summarize over 6 years of development on a single page, so you\'ll find here just a brief description of general server qualities.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"devstate\\"></a>Current development state</p><p class=\\"onas-paragraph\\">The most rational way to assess the current development state would be to look up our BugTracker statistics. Over the years thousands of players have been reporting all spotted bugs. At the moment of writing this, there are over 5300 bug reports fixed and only 35 bug reports open (unresolved). That\'s over 99% of bugs fixed! The BugTracker hasn\'t been cleared since May 2011.<br><br> Besides fixing the most obvious part, which is the content (quests, instances, spells, etc.), we have managed to rewrite major emulator structures and systems to eliminate more general bugs and problems, and also to optimize it. You\'ll find more informations below.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"performance\\"></a>Server performance</p><p class=\\"onas-paragraph\\">Firstly, we will have to explain what \\"update time diff\\" is (we usually refer to it as \\"diff\\" in short). Update time diff is the time it takes the server to process all current tasks. The longer the diff is, the longer you wait for your actions to be processed (running, casting spells, etc.) and the less dynamic the game is. Diff adds up to your network latency (explicitly shown in the game client) and creates final delay you experience while playing. You can check current and average diff (average in the last ~10 seconds) using \\".server info\\" command ingame, and also on our website by hovering your mouse over a realm.<br><br> We have managed to lower diffs to ~20ms (milliseconds) on average @ 2500 players online. Here is a <a href=\\"../img/_custom/diff/diff_2015-11-08.jpg\\" target=\\"_blank\\" data-mce-href=\\"../img/_custom/diff/diff_2015-11-08.jpg\\">screenshot</a>. We expect diffs to be ~40ms on average @ 4000 players online. We say \\"expect\\", because we haven\'t yet reached 4k since latest optimizations. However, we can guarantee the server easily supports 4000 players online, because it has already supported in the past. Here is a screenshot of 4000 players online with diffs ~65ms on average: <a href=\\"../img/_custom/diff/diff_2014-01-06.jpg\\" target=\\"_blank\\" data-mce-href=\\"../img/_custom/diff/diff_2014-01-06.jpg\\">click</a>.<br><br> Such exceptionally low diffs were achieved by countless optimizations over the years. Fortunately, we don\'t have to multiply our player counters by 2 to build up hype, as some popular servers do.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"movemaps\\"></a>MoveMaps</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">MoveMaps have been enabled in the whole game world since May 2013 and haven\'t been changed since then. Here is a video of how they are working: <a href=\\"http:/www.youtube.com/watch?v=EyPaoM9e3vk\\" target=\\"_blank\\" data-mce-href=\\"http:/www.youtube.com/watch?v=EyPaoM9e3vk\\">Sunwell MoveMaps</a>. Here is another video of them working in June 2012 (<a href=\\"http:/youtube.com/watch?v=hZfYkdGK3IE\\" target=\\"_blank\\" data-mce-href=\\"http:/youtube.com/watch?v=hZfYkdGK3IE\\">Sunwell MoveMaps 2012</a>), but only on battlegrounds and arenas back then.<br><br> Nice things to notice:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>working properly in standard places,</li><li>working in water,</li><li>working on the boundaries of water/land,</li><li>proper charge pathing,</li><li>NPCs take into consideration path existence when selecting a target from threat list,</li><li>NPCs evade after the target is unreachable for a few seconds, not sooner,</li><li>slightly different rules for pets.</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px;\\" data-mce-style=\\"margin-top: 0px;\\">&nbsp;<br> Current MoveMaps implementation is our own, original work. We are using a publicly available library to do the path calculations, while the usage of the library and incorporating it into core systems is our work. Thanks to that the MoveMaps are very fast, having almost no impact on server\'s performance.<br><br> MoveMaps are not perfect though, and never will be. They depend on pre-generated representation of visual textures, which is not ideal is some places.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"spellsystem\\"></a>Spell system</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">Generally speaking, we have rewritten spell, talent and dual talent specialization systems (September 2014). To be specific, we have completely rewritten the way spells and talents (both in core and database) are stored, added and removed, and rewritten dual talent specialization and talent resetting. This fixed very serious and unacceptable bugs, such as:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>talent bonuses applied twice,</li><li>talents working without any points spent,</li><li>doubled glyph effects,</li><li>possessing spells which should have been removed from a character,</li><li>and some more.</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px;\\" data-mce-style=\\"margin-top: 0px;\\">&nbsp;<br> Players hardly notice any difference, however the changes are significant, prevent many bugs, and positively affect balance in PvP and PvE.<br><br> Besides that, fully working character spells, talents and glyphs have always been our top priority. It happens that from time to time a new bug is discovered and reported. It is then fixed as soon as possible.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"spellstacking\\"></a>Spell stacking</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">We wrote our own spell stacking system (November 2012). This greatly influences balance in PvP and makes PvE harder, as it should be. Features:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Allows more sophisticated stacking rules, to reproduce all rules from the official servers.</li><li>Fixes stacking of many spells.</li><li>If required, allows to have multiple auras applied that shouldn\'t stack with each other. In such situations only the strongest effects of all the auras are working.</li><li>Implemented \\"A more powerful spell is already active\\".</li><li>Faster and simpler from technical point of view.</li><li>Working fully blizzlike.</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 6px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 6px;\\">&nbsp;<br> Examples:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Divine Spirit (80 spirit) + Fel Intelligence (48 intellect + 64 spirit) = 80 spirit + 48 intellect.</li><li>Battle Shout won\'t be applied to characters having a stronger Blessing of Might. Characters with a weaker effect or no effect at all will gain the buff properly.</li><li>Blessing of Kings and Blessing of Sanctuary will coexist on the target because of other bonuses, but bonus statistics won\'t accumulate.</li><li>Faerie Fire and Faerie Fire (Feral) from any number of casters will coexist on the target, because they give additional bonuses to the caster when having a talent, but the -5% armor effect won\'t accumulate.</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 30px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 30px;\\">&nbsp;</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"hotdot\\"></a>HoT and DoT calculation</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">We reworked HoT and DoT calculation (November 2012) to make it work the same as on the official servers. This influences balance in PvP and PvE. Changes:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Caster bonuses are calculated once, when the spell is cast, and are constant for the duration of the effect, not affected by the caster losing/gaining any buffs.</li><li>Target modifiers and bonuses affect the DoT/HoT up to date.</li><li>Percentage caster bonuses to damage/heal and to crit chance remain constant after first calculation, even if the aura is refreshed using some talents.</li><li>When refreshing DoT/HoT using talents, Spell Power bonus is recalculated at the moment of refreshing (and then constant again for the new duration).</li><li>DoTs i HoTs are now independent of caster existence. Thanks to that you may for example apply a DoT using Dancing Rune Weapon a second before the DRW disappears and the DoT will correctly deal damage afterwards.</li><li>Introduces \\"DoT rolling\\" mechanic, which is important during raids.</li><li>Fully blizzlike.</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 30px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 30px;\\">&nbsp;</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"instanceid\\"></a>Instance ID system</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">We have completely rewritten instance id system and character binding (December 2014). It eliminated some major bugs and fixed various related problems:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>fixed disappearing permanent instance binds (which allowed to run the same raid few times a week),</li><li>fixed automatic instance id reset randomly not working,</li><li>corrected bugs in creation, loading, closing and resetting of instance maps,</li><li>corrected bugs in group saving, loading and storing group data,</li><li>fixed a few other problems spotted on the occasion, including a few crashes.</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px;\\" data-mce-style=\\"margin-top: 0px;\\">&nbsp;<br> We have also implemented Instance Lock Extension function, allowing you to extend selected instance id periods. Instance Lock Extension allows every group to progress raids at their own pace. This was possible thanks to rewriting whole instance id system and eliminating huge amount of bugs in it. We also fixed displaying of global id reset times in the in-game calendar and displaying of your own instance lockout periods taking into account current extensions.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"dungeonfinder\\"></a>Dungeon Finder</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">We have fixed a lot of problems with the Dungeon Finder. The most significant change is our own system of queueing and joining players/groups together into DF groups. The publicly available one, copied to every emulator, performs poorly with lots of players queued. During peak hours, with thousands of players online, it checked less than 1% of all possible combinations of queued players. Our own system checks them all, properly forms groups and is hundreds of times faster. The changes (May 2013) include:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Properly joining players together into DF groups, no matter the number of players queued.</li><li>After a proposal fails, players who accepted it are put back to the beginning of the queue.</li><li>You can replace missing group members while inside the instance (by queueing DF again). New members will be teleported to leader\'s current location.</li><li>Dungeon rewards working for every dungeon, even the low level ones.</li><li>DF won\'t put groups into instances with mobs/bosses killed.</li><li>Implemented minimum level of gear requirement (only when accessing instances through DF).</li><li>Vote to kick works properly, with a reason to kick asked.</li><li>There are no bugs caused by being disconnected, entering and leaving the instance, and kicking or refilling players.</li><li>Luck of the Draw, Dungeon Cooldown and Dungeon Deserter buffs are working properly.</li><li>Groups are teleported to proper starting locations.</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 30px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 30px;\\">&nbsp;</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"bgqueues\\"></a>Battleground Queues</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">We have rewritten Battleground Queue system and everything related to it (July 2015). Not only lots of bugs were fixed, but also many improvements were introduced. A list of main changes:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Joined random and specific queues. Random queue will now gather groups together with specific queues.</li><li>Battlegrounds are created only after required amount of players is gathered for a particular Battleground. This helps to avoid premature closing of large Battlegrounds, caused by insufficient number of participants. In case of lower number of players queued, large Battlegrounds simply won\'t be created.</li><li>Already created Battlegrounds with free slots are filled up first using queued players. Only then new ones are created. When filling, Battlegrounds with more free slots (in percentage) are prioritized.</li><li>Fixed conditions for queueing as a group and queueing alone. You can queue for two different Battlegrounds at the same time without any bugs (as a group and alone).</li><li>Rewritten algorithms for assembling teams from queued groups/players. This applies to premade Battlegrounds, standard Battlegrounds, non-rated arenas and non-rated arenas against the same faction.</li><li>Fixed balancing of players in the opposite teams on Battlegrounds. The algorithm was reworked, and now takes into account actually invited characters and characters being teleported at the moment.</li><li>Fixed exceeding maximum number of players on either team.</li><li>Always sending proper queue and bg status to the game client.</li><li>And some more... ;)</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 30px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 30px;\\">&nbsp;</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"transports\\"></a>Transport System</p><p class=\\"onas-paragraph\\">For the typical motion transports (zeppelins, ships, etc.), we have fixed position desynchronization (visual vs actual) affecting a few of them, vmap issues and some bugs with fighting on such transports moving.<br><br> But most importantly, we have implemented static transports (July 2015), ie. elevators and trams. This means that players, pets, totems, traps, etc., now move along with elevators. Their position is updated both visually and actually according to the movement of the elevator. This applies to all kinds of transport, in every state, for any parameters, any rotation of the transport, rotation of it\'s path and rotation of it\'s animation. Also Line of Sight is properly blocked in the current position of every transport. This will surely be noticeable on Ring of Valor arena.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"instances\\"></a>Instances</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">Instead of waiting for hundreds of bugs to be reported, we decided to entirely rewrite every instance there is in the game. And by entirely we mean entirely (bosses, trash, scenes, achievements, etc.). Up to this moment, we have managed to rewrite:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>WotLK dungeons: 16 of 16</li><li>WotLK raids: 9 of 9</li><li>TBC dungeons: 16 of 16</li><li>TBC raids: 6 of 9</li><li>Vanilla dungeons: 17 of 19</li><li>Vanilla raids: 0 of 5</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 6px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 6px;\\">&nbsp;<br> Every instance in the game is open and available. Exact list of their current status is below.<br><br> <strong>WotLK dungeons:</strong></p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Halls of Reflection - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Pit of Saron - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Forge of Souls - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Trial of the Champion - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Culling of Stratholme - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Utgarde Pinnacle - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Halls of Lightning - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Oculus - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Halls of Stone - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Gundrak - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Violet Hold - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Drak\'Tharon Keep - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Ahn\'kahet: The Old Kingdom - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Azjol-Nerub - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Nexus - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Utgarde Keep - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 6px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 6px;\\">&nbsp;<br> <strong>WotLK raids:</strong></p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>The Ruby Sanctum - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Icecrown Citadel - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Onyxia\'s Lair - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Trial of the Crusader - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Ulduar - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Eye of Eternity - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Vault of Archavon - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Obsidian Sanctum - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Naxxramas - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 6px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 6px;\\">&nbsp;<br> <strong>TBC dungeons:</strong></p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Magisters\' Terrace - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Black Morass - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Arcatraz - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Botanica - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Mechanar - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Old Hillsbrad Foothills - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Shadow Labyrinth - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Shattered Halls - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Steamvault - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Sethekk Halls - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Auchenai Crypts - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Mana-Tombs - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Underbog - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Slave Pens - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Blood Furnace - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Hellfire Ramparts - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 6px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 6px;\\">&nbsp;<br> <strong>TBC raids:</strong></p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Sunwell Plateau - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Black Temple - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Battle for Mount Hyjal - <span style=\\"color: #bc9000;\\" data-mce-style=\\"color: #bc9000;\\">available, not rewritten yet</span></li><li>Tempest Keep: The Eye - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Serpentshrine Cavern - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Magtheridon\'s Lair - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Gruul\'s Lair - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Zul\'Aman - <span style=\\"color: #bc9000;\\" data-mce-style=\\"color: #bc9000;\\">available, not rewritten yet</span></li><li>Karazhan - <span style=\\"color: #bc9000;\\" data-mce-style=\\"color: #bc9000;\\">available, not rewritten yet</span></li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 6px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 6px;\\">&nbsp;<br> <strong>Vanilla dungeons:</strong></p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Scholomance - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Stratholme - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Dire Maul - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Blackrock Spire - <span style=\\"color: #bc9000;\\" data-mce-style=\\"color: #bc9000;\\">available, not rewritten yet</span></li><li>Blackrock Depths - <span style=\\"color: #bc9000;\\" data-mce-style=\\"color: #bc9000;\\">available, not rewritten yet</span></li><li>Temple of Atal\'Hakkar - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Maraudon - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Zul\'Farrak - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Uldaman - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Razorfen Downs - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Scarlet Monastery - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Razorfen Kraul - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Gnomeregan - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Stockade - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Blackfathom Deeps - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Shadowfang Keep - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>The Deadmines - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Wailing Caverns - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li><li>Ragefire Chasm - <span style=\\"color: green;\\" data-mce-style=\\"color: green;\\">rewritten and available</span></li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 6px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 6px;\\">&nbsp;<br> <strong>Vanilla raids:</strong></p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Temple of Ahn\'Qiraj - <span style=\\"color: #bc9000;\\" data-mce-style=\\"color: #bc9000;\\">available, not rewritten yet</span></li><li>Ruins of Ahn\'Qiraj - <span style=\\"color: #bc9000;\\" data-mce-style=\\"color: #bc9000;\\">available, not rewritten yet</span></li><li>Blackwing Lair - <span style=\\"color: #bc9000;\\" data-mce-style=\\"color: #bc9000;\\">available, not rewritten yet</span></li><li>Molten Core - <span style=\\"color: #bc9000;\\" data-mce-style=\\"color: #bc9000;\\">available, not rewritten yet</span></li><li>Zul\'Gurub - <span style=\\"color: #bc9000;\\" data-mce-style=\\"color: #bc9000;\\">available, not rewritten yet</span></li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 36px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 36px;\\">&nbsp;<br> We fixed some reported bugs in the not yet rewritten instances, but we are aware more needs to be done. Further reporting bugs in those instances is pointless, because they will be totally rewritten anyway. This list will then be updated accordingly.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"quests\\"></a>Quests</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">The Loremaster achievement has been obtainable since November 2012. We\'ve been fixing quests from the very beginning. We always try to fix them with proper visual effects, texts, waypoints, and so on. In some cases doing just the visual effects for a single quest took hours, and we did it anyway.<br><br> To our best knowledge, there\'s only one quest in the game that is not working, namely: The Battle For The Undercity. It is possible that some quests get stuck under certain circumstances, or are missing some visuals, or worked but got broken by an update. In such cases please report them on our BugTracker. We will fix them as soon as possible.<br><br> Here are just a few video examples of quests scripted by us:</p><ul class=\\"onas-paragraph\\" style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li><a href=\\"https:/www.youtube.com/watch?v=Qj54Gecz4oM\\" target=\\"_blank\\" data-mce-href=\\"https:/www.youtube.com/watch?v=Qj54Gecz4oM\\">Thrusting Hodir\'s Spear</a></li><li><a href=\\"https:/www.youtube.com/watch?v=Pg7aPvUeIgE\\" target=\\"_blank\\" data-mce-href=\\"https:/www.youtube.com/watch?v=Pg7aPvUeIgE\\">Army of the Damned</a></li><li><a href=\\"https:/www.youtube.com/watch?v=dCJ23A3BYfA\\" target=\\"_blank\\" data-mce-href=\\"https:/www.youtube.com/watch?v=dCJ23A3BYfA\\">The Sum is Greater than the Parts</a></li><li><a href=\\"https:/www.youtube.com/watch?v=PGxoMyCVdEM\\" target=\\"_blank\\" data-mce-href=\\"https:/www.youtube.com/watch?v=PGxoMyCVdEM\\">Watts My Target</a></li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 30px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 30px;\\">&nbsp;</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"pets\\"></a>Pets</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">Various fixes have been done to pets in order to achieve their correct behavior:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>If a pet is ordered to cast a spell, but the target is out of range or not in LoS, the pet will run up to the target and then cast the ordered spell (no need to order it again).</li><li>Properly working defensive stance (aggressive and passive too).</li><li>All pets have AoE damage reduction aura.</li><li>Correctly inheriting owner\'s statistics.</li><li>Pets react to crowd control auras on targets.</li><li>Correct pet size scaling with it\'s level.</li><li>Fixed controlling pets if one of them is dead (eg. Shaman\'s Feral Spirit).</li><li>Fixed dismissing dead pets.</li><li>Damage done by and to pets is properly counted in Battleground statistics.</li><li>And many more.</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 30px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 30px;\\">&nbsp;<br> Moreover, nearly all non-combat pets have their custom animations and behaviors scripted.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"battlegrounds\\"></a>Battlegrounds and arenas</p><p class=\\"onas-paragraph\\">We have completely rewritten Warsong Gulch, Arathi Basin and Eye of the Storm, and fixed reported bugs in the rest of the Battlegrounds. We consider rewriting the remaining three in the future, just to be sure everything is top-notch.<br><br> All Battleground achievements are working. Experience is properly awarded on Battlegrounds for killing, various bg actions and winning/losing. There wasn\'t much to fix on arenas. We have scripted the Ring of Valor arena - working elevators/pillars, MoveMaps and LoS smoothly matching pillar movement.<br><br> Battleground Queues and Arena Matching have been rewritten, which is described in detail in other sections on this page.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"dynamicmmr\\"></a>Dynamic MMR</p><p class=\\"onas-paragraph\\">MMR (Match Making Rating) is a rating for each player that changes every time you play an arena battle. Your MMR increases when you win, and it decreases when you lose. It is used to pair teams of similar skill. But there\'s a dillema: lower allowed difference in MMR results in fairer matches, but increases waiting time in the queue. Solution: Dynamic MMR.<br><br> The allowed MMR difference between two teams grows as you wait in the queue. The most adequate opponents are searched for in larger and larger span of MMR values as you wait. Previous algorithm for matching arena teams had a constant MMR difference set and a lot of defects, so we rewrote it completely.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"achievements\\"></a>Achievements</p><p class=\\"onas-paragraph\\">Every achievement in the game should be obtainable. To give you an example, just imagine ~140 achievements in Ulduar instance alone and every one of them working properly. We have paid close attention to achievements in every instance we have rewritten. There may be some difficulties with obtaining achievements in the not yet rewritten instances, but they should be doable. Achievements of all the categories are working (General, Quests, Exploration, PvP, Dungeons &amp; Raids, Professions, Reputation, World Events).</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"events\\"></a>Ingame seasonal events</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">We have rewritten every seasonal event in the game. All achievements from the events are working, together with the \\"What A Long, Strange Trip It\'s Been\\" achievement, awarded for completing all seasonal achievements. The list of available and working seasonal events:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Lunar Festival</li><li>Love is in the Air</li><li>Noblegarden</li><li>Children\'s Week</li><li>Midsummer Fire Festival</li><li>Brewfest</li><li>Hallow\'s End</li><li>Day of the Dead</li><li>Pilgrim\'s Bounty</li><li>Feast of Winter Veil</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 6px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 6px;\\">&nbsp;<br> The following are also fixed:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Gurubashi Arena event</li><li>Stranglethorn Fishing Extravaganza</li><li>Kalu\'ak Fishing Derby</li><li>Elemental Invasions from Vanilla</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 30px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 30px;\\">&nbsp;</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"wintergrasp\\"></a>Wintergrasp</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">Countless fixes have been applied to the Wintergrasp. Here are the most important ones:</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>Increased visibility distance.</li><li>Fixed Wintergrasp ranks. Higher ranks are gained for killing players, turrets and Keep NPCs.</li><li>Fixed applying Tenacity and fixed it\'s formula.</li><li>Players are always rewarded, regardless being dead or alive.</li><li>All players in range are credited for certain actions.</li><li>To drive vehicles you have to have adequate rank obtained. Additional vehicle seats do not require ranks.</li><li>Players are automatically put into vehicles after they are built.</li><li>Fixed trajectory spells target selection.</li><li>Fixed additional bonuses for damaging/destroying towers.</li><li>Wintergrasp weekly quests are pooled (only 1 weekly available at a time) and available both inside and outside the Fortress.</li><li>Player corpses can be looted.</li><li>Minor and Major Elementals disappear during the fight.</li><li>Major Elementals are only visible to the faction controlling the Wintergrasp.</li><li>Lots of general changes to the vehicles.</li><li>And many more...</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 30px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 30px;\\">&nbsp;</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"raidbrowser\\"></a>Raid Browser</p><p class=\\"onas-paragraph\\" style=\\"margin-bottom: 6px;\\" data-mce-style=\\"margin-bottom: 6px;\\">We share with you written from scratch Raid Browser and our AddOn for it (February 2015).<br> Raid Browser can be used to find players interested in doing specific raids from any expansion. It\'s a kind of announcement table, where you can list yourself alone or as group, adding a brief comment (eg. your requirements, expectations, gs, \\"I have achievement X\\").<br> Important: Raid Browser is not Raid Finder. It won\'t assemble groups. You still have full control over choosing group members.<br><br> Raid Browser is available after typing /rb or in Social (O) -&gt; Raid tab -&gt; Raid Browser.<br><br> Why is it worth using?</p><ul style=\\"margin: 0px 60px 0px 60px; color: #000;\\" data-mce-style=\\"margin: 0px 60px 0px 60px; color: #000;\\"><li>You get a lot of info about a character before even writing a private message. About every listed player in RB you will get such informations as: level, class, talents, avg itemLevel, role (tank / healer / dps), comment, a list of killed bosses if the player has an ID, spell power, attack power, heal power, melee crit rating, ranged crit rating, spell crit rating, defense skill, dodge rating, block rating, parry rating, haste rating, expertise rating and other.</li><li>No need to read repeated spam on lfg channel.</li></ul><p class=\\"onas-paragraph\\" style=\\"margin-top: 0px; margin-bottom: 30px;\\" data-mce-style=\\"margin-top: 0px; margin-bottom: 30px;\\">&nbsp;<br> Raid Browser is working without the AddOn, but the default interface won\'t display all of this data.<br> Link to the AddOn: <a href=\\"../SunwellRB.rar\\" data-mce-href=\\"../SunwellRB.rar\\">download</a>.<br> Screenshots: <a href=\\"../img/_custom/RB_solo.jpg\\" target=\\"_blank\\" data-mce-href=\\"../img/_custom/RB_solo.jpg\\">solo example</a>, <a href=\\"../img/_custom/RB_group.jpg\\" target=\\"_blank\\" data-mce-href=\\"../img/_custom/RB_group.jpg\\">group example</a>.<br><br> We encourage to use Raid Browser and propagate using it. This way everyone will more easily find a group.</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"arenaspectator\\"></a>Arena Spectator</p><p class=\\"onas-paragraph\\">We share with you our Arena Spectator system and an AddOn for it (December 2013).<br> Arena Spectator can be used to watch live arena matches with all the details displayed.<br> Here\'s a video showing it in action: <a href=\\"https:/www.youtube.com/watch?v=GBYBN2o7RFM\\" target=\\"_blank\\" data-mce-href=\\"https:/www.youtube.com/watch?v=GBYBN2o7RFM\\">Sunwell Arena Spectator</a>.<br> Link do the AddOn: <a href=\\"../SunwellAS.rar\\" data-mce-href=\\"../SunwellAS.rar\\">download</a>.<br> To leave the arena before it ends, type: /g .sp leave</p><p class=\\"onas-subtitle\\"><a class=\\"mceItemAnchor\\" name=\\"summary\\"></a>Summary</p><p class=\\"onas-paragraph\\">Sounds too good to be true? Check it out yourself ;) With passion and dedication, we\'ve been doing as much as we could and have always strived to deliver highest quality possible. There are no promises. Everything is already here, and more is comming. If you are looking for the best Wrath of the Lich King experience, you have come to the right place.<br><br> Reading through the changes is relatively quick, compared to actually making them. Lots of the changes described by a single sentence took many days and sleepless nights. We would like to clearly state this: the server is not perfect. No server will ever be. We are doing our best. No more and no less.<br><br> We hope you\'ll enjoy your adventure on Sunwell.</p>","polish":"<p>TODO - switch to EN language.</p>"}', NULL, 1),
	(4, 'premium', '{"english":"Premium","polish":"Premium"}', '{"english":"<p>TODO - Switch to PL language</p>","polish":"<p class=\\"premium-subtitle\\">Co to jest PP?</p><p class=\\"premium-paragraph\\">PP, czyli Punkty Premium, są specjalną walutą dostępną na serwerze, za którą można uzyskać różnego rodzaju dodatki umilające i/lub ułatwiające rozgrywkę. Ważne:&nbsp;<strong>żadne dostępne za PP opcje nie wpływają na przewagę gracza w walce</strong>. PP dają możliwość urozmaicenia gry, są sposobem na wyróżnienie się z tłumu i oszczędzenie czasu. Ze środków uzyskanych z PP administracja utrzymuje serwer.</p><p class=\\"premium-subtitle\\">Jak zdobyć PP?</p><p class=\\"premium-paragraph\\">PP może zdobyć w panelu gracza każdy, kto ma zarejestrowane konto. Aby dostać się do panelu gracza należy zalogować się na stronie, używając loginu i hasła z gry. Są 3 sposoby na pozyskanie PP:</p><div class=\\"terms\\"><ol><li>Wysłanie SMSa<br>wysyłasz sms na podany numer i o określonej treści, a po chwili otrzymujesz sms zwrotny z kodem, który należy wpisać w określnym miejscu. Numery, treść smsów, koszt oraz ilość PP jakie uzyskasz za dany sms są rozpisane&nbsp;<a href=\\"http://sunwell.pl/ucp\\" target=\\"_blank\\" data-mce-href=\\"http://sunwell.pl/ucp\\">tutaj</a>. Pod informacją o smsach znajduje się pole, w które należy wpisać otrzymany kod i zatwierdzić. Jeśli wprowadzony kod jest poprawny, Twoje konto zostanie natychmiast doładowane o określoną ilość PP. Nie jesteśmy odpowiedzialni za wysyłanie SMSów, więc wszelkie skargi związane z nieotrzymaniem kodu prosimy kierować do MobilePay.<br>Operatorem usługi \\"Kody Dostępu SMS\\" jest&nbsp;<a href=\\"http://www.mobilepay.pl/\\" target=\\"_blank\\" data-mce-href=\\"http://www.mobilepay.pl/\\">MobilePay</a>.<br>Koniecznie przeczytaj&nbsp;<a href=\\"https://www.mobilepay.pl/Regulations/Regulamin%20Kody%20Dostepu%20SMS.pdf\\" target=\\"_blank\\" data-mce-href=\\"https://www.mobilepay.pl/Regulations/Regulamin%20Kody%20Dostepu%20SMS.pdf\\">regulamin usługi</a>.</li><li>Przelew<br>kwoty przelewów oraz ilość PP jaką uzyskasz za dany przelew są rozpisane&nbsp;<a href=\\"http://sunwell.pl/ucp\\" target=\\"_blank\\" data-mce-href=\\"http://sunwell.pl/ucp\\">tutaj</a>. Wybierasz interesującą Cię opcję i klikasz w odpowiadający jej odnośnik. Następnie postępujesz zgodnie z instrukcją. Pamiętaj: podaj poprawne dane osobowe, szczególnie adres e-mail! To właśnie na ten adres otrzymasz kod, który należy wprowadzić na stronie (kod otrzymasz, gdy tylko DotPay potwierdzi wpłynięcie środków). Pole do wpisywania kodów za przelew znajduje się na w/w stronie. Jeśli wprowadzony kod jest poprawny, Twoje konto zostanie natychmiast doładowane o określoną ilość PP.<br>Przelewy obsługuje&nbsp;<a href=\\"http://dotpay.pl/\\" target=\\"_blank\\" data-mce-href=\\"http://dotpay.pl/\\">DotPay</a>.<br>Konto, na którym rejestrowany jest kod z przelewu, otrzymuje dodatkowy przywilej: iluzję. Więcej informacji znajdziesz niżej.</li><li>Bazar<br>więcej informacji o bazarze znajdziesz niżej.</li></ol></div><p>&nbsp;</p><p class=\\"premium-subtitle\\">Przywileje</p><p class=\\"premium-paragraph\\">Są to modyfikacje w grze, przypisywane do konta, a więc działające na wszystkich postaciach znajdujących się na koncie. Przywileje dzielimy na 2 główne grupy: pasywne - działają bez ingerencji gracza, oraz aktywne - korzysta się z nich za pomocą specjalnych komend. Przywileje można dodać do konta&nbsp;<a href=\\"http://sunwell.pl/ucp\\" target=\\"_blank\\" data-mce-href=\\"http://sunwell.pl/ucp\\">tutaj</a>. W nawiasach, obok nazwy, podane są ceny (dzień = 24 godziny). Jeśli w trakcie dodawania przywileju jest on już aktywny na koncie, to do pozostałego czasu zostanie dodana wykupiona ilość dni. Przed korzystaniem wyloguj się z konta!<br><br><strong>Opisy obecnie dostępnych przywilejów:</strong></p><div class=\\"terms\\"><ul><li><strong>Teleport</strong>: przywilej aktywny; umożliwia teleport w wybrane miejsce. Szczegółowy opis znajduje się&nbsp;<a href=\\"http://www.sunwell.pl/forum//index.php?showtopic=5175\\" target=\\"_blank\\" data-mce-href=\\"http://www.sunwell.pl/forum//index.php?showtopic=5175\\">tutaj</a>.</li><li><strong>No Resurrection Sickness</strong>: przywilej pasywny; brak choroby pośmiertelnej.</li><li><strong>EXP BOOST</strong>: przywilej pasywny; doświadczenie uzyskane w grze jest podwajane. Exp boost i status rested (niebieski pasek expa) to dwie różne i niezależne rzeczy!</li><li><strong>No Durability Loss</strong>: przywilej pasywny; Twoje itemy nie ulegają zniszczeniu, tzn. durability itemów nie spada podczas używania/śmierci.</li><li><strong>Illusion</strong>: przywilej aktywny; umożliwia zmianę wyglądu postaci. Dostępny tylko za przelew. Więcej informacji&nbsp;<a href=\\"http://www.sunwell.pl/forum//index.php?showtopic=6074\\" target=\\"_blank\\" data-mce-href=\\"http://www.sunwell.pl/forum//index.php?showtopic=6074\\">tutaj</a>.</li><li><strong>Instant Flight Paths</strong>: przywilej pasywny; przelot gryfem / wiverną jest natychmiastowy.</li></ul></div><p>&nbsp;</p><p class=\\"premium-subtitle\\">Usługi</p><p class=\\"premium-paragraph\\">Są jednokrotnym wykonaniem wybranej czynności. Z usług można skorzystać&nbsp;<a href=\\"http://sunwell.pl/ucp\\" target=\\"_blank\\" data-mce-href=\\"http://sunwell.pl/ucp\\">tutaj</a>. W nawiasach, obok nazwy, podane są ceny. Po wskazaniu danej opcji z listy pojawiają się dodatkowe pola do wypełnienia. Przed korzystaniem wyloguj się z konta!<br><br><strong>Opisy obecnie dostępnych usług:</strong></p><div class=\\"terms\\"><ul><li><strong>Recustomize</strong>: Umożliwia zmianę wyglądu, płci i nicku postaci. Wybieramy postać i klikamy przycisk \\"Wykonaj\\". Po zalogowaniu się na konto, na ekranie wyboru postaci obok wskazanej postaci pojawi się duża ikonka, która po kliknięciu umożliwia dowolną zmianę wyglądu, płci i nicku postaci. Oczywiście możemy nie dokonywać żadnych zmian.</li><li><strong>Zmiana Nicku</strong>: Wybieramy postać i klikamy przycisk \\"Wykonaj\\". Przy próbie wejścia na daną postać zostaniemy poproszeni o podanie nowego nicku.</li><li><strong>Zmiana Rasy</strong>: Umożliwia zmianę rasy w obrębie frakcji. Wybieramy postać i klikamy przycisk \\"Wykonaj\\". Po zalogowaniu się na konto, na ekranie wyboru postaci obok wskazanej postaci pojawi się duża ikonka, która po kliknięciu umożliwia zmianę rasy oraz dostosowanie nowego wyglądu i ewentualnie nowego nicku. Uwaga: przed użyciem upewnij się, że Twoja klasa może mieć inną rasę w obrębie frakcji!</li><li><strong>Zmiana Frakcji</strong>: Umożliwia zmianę frakcji postaci. Wybieramy postać i klikamy przycisk \\"Wykonaj\\". Po zalogowaniu się na konto, na ekranie wyboru postaci obok wskazanej postaci pojawi się duża ikonka, która po kliknięciu umożliwia zmianę frakcji oraz dostosowanie nowego wyglądu i ewentualnie nowego nicku. Zasady:<br>- Postać musi mieć co najmniej 10 level, a Death Knight 60.<br>- Postacie w gildii nie mogą zmienić frakcji. Należy ręcznie wyjść z gildii.<br>- Leader teamu arenowego nie może zmienić frakcji. Jeśli postać jest leaderem, to musi przekazać komuś przywództwo lub rozwiązać team. Podczas zmiany frakcji postać automatycznie wychodzi z teamu arenowego.<br>- Nie można zmienić frakcji posiadając maile w skrzynce (włącznie z mailami, które jeszcze nie doszły) lub biorąc udział w aukcjach. Należy najpierw wyjąć wszystko ze skrzynki, usuwając przy tym wiadomości oraz zakończyć działania w auction housie. Jakiekolwiek itemy w skrzynce mailowej, na aukcjach, gold wydany na oferty aukcyjne - mogłyby zostać utracone podczas zmiany frakcji.<br>- Elementy takie jak itemy, achievementy, questy, reputacje, spelle, tytuły - mogą być zależne od aktualnej frakcji i mieć swoje odpowiedniki w przeciwnej frakcji. W związku z tym są one konwertowane podczas zmiany frakcji na ich odpowiedniki, jeśli je mają.<br>- Konwersja wyżej wymienionych elementów to tysiące różnych reguł. Dołożyliśmy wszelkich starań, aby każdy pojedynczy element był poprawnie przenoszony, jednak nie jesteśmy w stanie tego zagwarantować. Wszelkie straty/uszkodzenia podczas zmiany frakcji nie będą w żadnym wypadku zwracane/naprawiane. Prosimy zgłaszać błędy konwersji na BugTrackerze.<br>- Itemy premium także podlegają konwersji.<br>- Id na instancje nie jest resetowane.<br>- Wszystkie aktywne questy zostaną wyczyszczone z quest loga.<br>- Wykorzystywanie błędów zmiany frakcji do uzyskania jakichś korzyści będzie skutkowało banicją. Wszelkie możliwości nadużyć należy niezwłocznie zgłosić na BugTrackerze.<br>- Nieopisane kwestie mogą, ale nie muszą, działać tak jak na oficjalnym serwerze.<br>- Zmiana frakcji oznacza akceptację powyższych zasad.</li><li><strong>Przelew PP</strong>: Wpisujemy nazwę konta (nie nazwę postaci) oraz ilość PP, jaką chcemy przelać i zatwierdzamy. PP zostaną przelane natychmiastowo.</li><li><strong>Transfer Postaci</strong>: Umożliwia przeniesienie wybranej postaci na inne konto na tym samym realmie. Wybieramy postać i wpisujemy nazwę konta docelowego.</li><li><strong>All Flight Paths</strong>: Odkrywa wszystkie Flight Paths na wybranej postaci. Wybieramy postać z listy i klikamy przycisk \\"Wykonaj\\".</li><li><strong>Przywracanie Postaci</strong>: Umożliwia przywrócenie usuniętej postaci, która posiadała level 70 lub wyższy, przez 30 dni od momentu jej usunięcia. Wybieramy postać z listy i klikamy przycisk \\"Wykonaj\\".</li><li><strong>Zamrażanie Konta</strong>: Jeśli z pewnych przyczyn zamierzasz nie logować się do gry przez dłuższy czas (ponad kilka miesięcy), to ta opcja umożliwia zamrożenie konta, aby nie zostało usunięte podczas czyszczenia nieużywanych kont. Status zamrożenia znika wraz z zalogowaniem się do gry.</li></ul></div><p>&nbsp;</p><p class=\\"premium-subtitle\\">Itemy</p><p class=\\"premium-paragraph\\">W panelu gracza, w menu, na rozwijanej liście Premium znajdują sie poszczególne typy itemów. Po wybraniu odpowiedniego typu mamy przed sobą listę dostępnych itemów, z której zaznaczamy jeden przedmiot. Poniżej wybieramy postać, na którą chcemy wysłać dany item i klikamy przycisk Wyślij. Po przelogowaniu się item trafi na naszą skrzynkę pocztową (upewnij się, że masz w niej miejsce).</p><p class=\\"premium-subtitle\\">Bazar</p><p class=\\"premium-paragraph\\">Do bazaru można się dostać w panelu gracza, wybierając tę opcję z rozwijanego menu Premium.<br>Gracze posiadający PP mogą je wystawić na bazarze za pożądaną ilość złota. Oferty te są widoczne dla wszystkich i, jeśli tylko posiadamy odpowiednią ilość złota, możemy je wykupić. Złoto zostanie zabrane z wybranej postaci, przekazane na konto wystawiającego, a PP niezwłocznie dodane do naszego konta. Dostępne oferty, bez wyjątku, są wystawione przez innych graczy serwera. Wyświetlane jest 100 najbardziej opłacalnych do kupienia ofert. Najbardziej opłacalne opcje znajdują się na samym początku listy. Pieniądze zarobione na bazarze można wypłacić na wybraną postać, która nie jest aktualnie zalogowana w grze.<br>Bazar jest jedynym bezpiecznym miejscem do wymiany PP za Gold. Jeśli chcesz ryzykować i wymieniać się w jakikolwiek inny sposób - robisz to na własną odpowiedzialność!<br><br>Każdy realm posiada osobny bazar, po to, by nie przesyłać golda między realmami. Zwróć szczególną uwagę z którego bazaru korzystasz!</p><p class=\\"premium-subtitle\\">Slave Market</p><p class=\\"premium-paragraph\\">Slave Market (SM) jest miejscem do bezpiecznego handlu postaciami, czyli wymian lub sprzedaży za PP. Handel w jakimkolwiek innym miejscu jest zakazany, a skradzione postacie nie są i nie będą odzyskiwane, ponieważ to wasza sprawa komu podajecie hasło do konta.<br>SM wymaga&nbsp;cywilizowanej przeglądarki internetowej&nbsp;(najlepiej Chrome, Firefox, Opera)&nbsp;w uaktualnionej wersji. W starszych wersjach lub kartoflanych przeglądarkach mogą wystąpić problemy lub market może w ogóle nie działać.<br>Na początku trzeba wrzucić postać na SM. W tym celu należy się wylogować z konta i kliknąć tę akcję dla wybranej postaci na liście. Jednorazowy koszt wgrania postaci wynosi 1500PP i nie ma żadnych innych kosztów. Jeśli nie wymienisz się tą postacią i postanowisz odesłać ją na konto, to odzyskasz PP. Jednak aby uniknąć trollowania ofertami, po 7 dniach publicznej oferty, czyli takiej widocznej dla wszystkich, PP nie zostanie oddane podczas przywracania postaci na konto, ale można nią nadal wystawiać oferty. Postacią znajdującą się na SM można handlować nieograniczoną ilość razy. Wgranie postaci odłącza ją od konta, uniemożliwiając jakiekolwiek zmiany oraz odświeża jej profil w armory dodając do niego listę wszystkich zakładalnych itemów w bagach i banku oraz listę wszystkich tytułów, mountów i non-combat petów. Wymagany jest 10 level postaci.<br>Mając postać na SM możemy stworzyć publiczną ofertę, precyzując dokładnie czego oczekujemy w zamian lub możemy przeglądać oferty innych i składać im propozycje, jeśli spełniamy ich wymagania. W przypadku proponowania PP do wymiany dochodzi natychmiast. Gdy proponujemy postać - wystawiający ofertę ma możliwość zaakceptowania lub odrzucenia takiej propozycji.<br>Lista ofert i propozycji jest aktualizowana wraz z załadowaniem strony. Bardzo złym nawykiem jest odświeżanie strony wysyłającej dane do serwera klawiszem F5 lub przyciskiem odśwież wbudowanym w przeglądarkę, ponieważ odświeżenie strony powoduje ponowne wysłanie żądań do serwera. Najlepszym sposobem na aktualizację danych jest kliknięcie w pasek adresu i naciśnięcie entera.<br>Po dokonaniu wymiany wysyłamy nową postać na nasze konto lub cieszymy się zarobionymi punktami premium :)</p><p class=\\"premium-subtitle\\">Reklamacje</p><p class=\\"premium-paragraph\\">Administracja nie ponosi odpowiedzialności za błędy użytkownika i/lub niestosowanie się do poleceń. Jeśli wszystko wykonałeś zgodnie z instrukcją, a mimo to wystąpiły jakieś problemy - napisz na adres&nbsp;TODO@TODO.TODO ze szczegółowym opisem problemu. Maile bez wszystkich niezbędnych informacji (login, nazwa postaci, REALM, ewentualnie kod) będą ignorowane.</p>"}', NULL, 1),
	(5, 'ticketrules', '{"english":"GM ticket rules","polish":"Zasady ticketów do GM"}', '{"english":"<p>TODO</p>","polish":"<p>TODO</p>"}', NULL, 1);

DROP TABLE IF EXISTS `sideboxes`;
CREATE TABLE IF NOT EXISTS `sideboxes` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `type` varchar(50) NOT NULL DEFAULT '',
  `displayName` text,
  `rank_needed` int(10) NOT NULL DEFAULT '1',
  `order` int(11) DEFAULT '100',
  `permission` varchar(50) DEFAULT NULL,
  `boxVisible` tinyint(1) NOT NULL DEFAULT '1',
  `titleVisible` tinyint(1) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`),
  KEY `fk_sb_rank_needed` (`rank_needed`),
  CONSTRAINT `fk_sb_rank_needed` FOREIGN KEY (`rank_needed`) REFERENCES `ranks` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

INSERT INTO `sideboxes` (`id`, `type`, `displayName`, `rank_needed`, `order`, `permission`, `boxVisible`, `titleVisible`) VALUES
	(1, 'status', '{"english":"Server status"}', 1, 1, NULL, 1, 1),
	(2, 'info_login', '{"english":"User area"}', 2, 2, NULL, 1, 0);

